        fbUpdateRequests: function (sock, cleanDirty, fb_width, fb_height) {
            var offsetIncrement = 0;

            var cb = cleanDirty.cleanBox;
            var w, h;
            if (cb.w > 0 && cb.h > 0) {
                w = typeof cb.w === "undefined" ? fb_width : cb.w;
                h = typeof cb.h === "undefined" ? fb_height : cb.h;
                // Request incremental for clean box
                RFB.messages.fbUpdateRequest(sock, 1, cb.x, cb.y, w, h);
            }

            for (var i = 0; i < cleanDirty.dirtyBoxes.length; i++) {
                var db = cleanDirty.dirtyBoxes[i];
                // Force all (non-incremental) for dirty box
                w = typeof db.w === "undefined" ? fb_width : db.w;
                h = typeof db.h === "undefined" ? fb_height : db.h;
                RFB.messages.fbUpdateRequest(sock, 0, db.x, db.y, w, h);
            }
        },

        fbUpdateRequest: function (sock, incremental, x, y, w, h) {
            var buff = sock._sQ;
            var offset = sock._sQlen;

            if (typeof(x) === "undefined") { x = 0; }
            if (typeof(y) === "undefined") { y = 0; }

            buff[offset] = 3;  // msg-type
            buff[offset + 1] = incremental;

            buff[offset + 2] = (x >> 8) & 0xFF;
            buff[offset + 3] = x & 0xFF;

            buff[offset + 4] = (y >> 8) & 0xFF;
            buff[offset + 5] = y & 0xFF;

            buff[offset + 6] = (w >> 8) & 0xFF;
            buff[offset + 7] = w & 0xFF;

            buff[offset + 8] = (h >> 8) & 0xFF;
            buff[offset + 9] = h & 0xFF;

            sock._sQlen += 10;
        },

        // UTF-8 compatible string
        clientCutUTF8Text: function (sock, text) {
            var buff = sock._sQ;
            var offset = sock._sQlen;

            buff[offset] = 6; // msg-type

            buff[offset + 1] = 0; // padding
            buff[offset + 2] = 0; // padding
            buff[offset + 3] = 0; // padding

            var n = text.length;
            var codeAt = text.codePointAt ? text.codePointAt : text.charCodeAt;

            for (var i = 0, idx = 0; i < n; i++) {
                var code = codeAt.call(text, i);

                ///console.log("clientCutText,codePoint: %d", code);

                // check utf8 
                if (code > 0xffff) {
                    buff[offset + 8 + idx] = ((code >> 18) & 0x07) | 0xf0; idx ++;
                    buff[offset + 8 + idx] = ((code >> 12) & 0x3f) | 0x80; idx ++;
                    buff[offset + 8 + idx] = ((code >>  6) & 0x3f) | 0x80; idx ++;
                    buff[offset + 8 + idx] = ((code >>  0) & 0x3f) | 0x80; idx ++;
                } else 
                if (code > 0x7ff) {
                    buff[offset + 8 + idx] = ((code >> 12) & 0x0f) | 0xe0; idx ++;
                    buff[offset + 8 + idx] = ((code >>  6) & 0x3f) | 0x80; idx ++;
                    buff[offset + 8 + idx] = ((code >>  0) & 0x3f) | 0x80; idx ++;
                } else 
                if (code > 0x7f) {
                    buff[offset + 8 + idx] = ((code >>  6) & 0x1f) | 0xc0; idx ++;
                    buff[offset + 8 + idx] = ((code >>  0) & 0x3f) | 0x80; idx ++;
                } else {
                    buff[offset + 8 + idx] = ((code >>  0) & 0x7f) | 0x00; idx ++;
                }
            }
            // ???append '\0' for c string
            ///buff[offset + 8 + idx] = 0; idx ++;

            buff[offset + 4] = idx >> 24;
            buff[offset + 5] = idx >> 16;
            buff[offset + 6] = idx >> 8;
            buff[offset + 7] = idx;

            sock._sQlen += 8 + idx;
        }
    };

    RFB.genDES = function (password, challenge) {
        var passwd = [];
        for (var i = 0; i < password.length; i++) {
            passwd.push(password.charCodeAt(i));
        }
        return (new DES(passwd)).encrypt(challenge);
